import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom'
import axios from 'axios';

import MenuG from '../../components/hamburgerButton';

import Header from '../../components/header';
import SideBar from '../../components/formSideBar';

import Form from '../../components/form';

function RequestForm() {

  var [loading, setLoading] = useState(true);
  var [admin, setAdmin] = useState(false);
  var [requestForm, setRequestForm] = useState(true);

  var history = useHistory();

  useEffect(() => {
    setLoading(true)
    axios
      .get("http://localhost:3002/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      })
      .then((result) => {
        console.log(result)
        console.log(result.data.roles.includes("3_ROLE_ADMIN"))
        var resposta = result.data.roles.includes("3_ROLE_ADMIN");

        console.log(resposta)
        if (resposta == true) {
          setAdmin(true)
        }
        else {
          setAdmin(false)
        }
        setLoading(false)
      })
  }, []);
  return (
    <>
      {loading ? <>Loading...</> :
        <>
          <MenuG />
          <Header />
          <SideBar admin = {admin} requestForm  = {requestForm}/>
          <Form />
        </>
      }
    </>
  );
}

export default RequestForm;
