import React, { useState } from 'react';
import { FaFileImport } from 'react-icons/fa';
import { FaPrint } from 'react-icons/fa';
import './styles.scss';
import axios from 'axios';

export default function Form() {
  //cursos
  const [course, setCourse] = useState(0);
  // const [posGraduacao, setPosGraduacao] = useState('');

  var curso;
  // var detalheGraduacao;

  if (course === "1") {
    curso = 1;
    // detalheGraduacao = '';
  } else if (course === "2") {
    curso = 2;
    // detalheGraduacao = '';
  } else if (course === "3") {
    curso = 3;
    // detalheGraduacao = '';
  } else if (course === "4") {
    curso = 4;
    // detalheGraduacao = posGraduacao;
  }

  // centro de custos
  const [cc, setCc] = useState('');

  var centro_custos;

  if (cc === "1") {
    centro_custos = 1;
  } else if (cc === "2") {
    centro_custos = 2;
  } else if (cc === "3") {
    centro_custos = 3;
  } else if (cc === "4") {
    centro_custos = 4;
  } else if (cc === "5") {
    centro_custos = 5;
